package com.trainning.basics;

public class HalfUpto {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		  int rows = 5; // Number of rows in the pyramid
	        
	        for (int i = 1; i <= rows; i++) {
	            for (int j = 1; j <= i; j++) {
	                System.out.print("* ");
	            }
	            System.out.println();
	}

}
}
